xpath.script:
var cont = 0;
$("#icon_nav_reliant_1_movil").click(function(){
    console.log("aparece menu")
    $('#menu_options_reliant_1_movil').show();
});
$(document).on("click",function(e) {   
cont = cont +1;                      
    var container = $("#menu_options_reliant_1_movil");

    if (!container.is(e.target) && container.has(e.target).length === 0 && cont>1) { 
        $('#menu_options_reliant_1_movil').hide(); 
        cont = 0;           
    }    

});

t.script:

window.onload = function readySustainability(){
    var id_prim = $( ".test_class" ).data().test_var; 
    var id_values_type_filtro = $(this).attr("id");
    var selector_oculta = "div.show_init:not([id='" + String(id_values_type_filtro) + "'])";
    var selector_muestra = "div.show_init[id='" + String(id_prim) + "']";
    $(selector_oculta).hide();
    $(selector_muestra).show();

    var selector_pinta_option = "div.reliant_sustent_3_1[id='" + String(id_prim) + "']";
    $(selector_pinta_option).addClass('sustent_active') 
}

